mapper.select:
select count(*)
from system.task t
where t.create_time between #{startTime} and #{endTime}

    and t.task_status = #{taskStatus}

SELECT DISTINCT t.id taskId, dd.code_name taskType, t.start_time startTime, t.end_time endTime, CONCAT(b.location,cc.`name`) inspectionPosition, u.real_name inspectorName, d.code_name taskStatus FROM system.t_task t LEFT JOIN system.`user` u ON t.inspector_id = u.id LEFT JOIN system.t_task_bridge_component tc ON t.id = tc.task_id LEFT JOIN system.t_bridge_info_component bc ON tc.bridge_component_id = bc.id LEFT JOIN system.t_component c ON bc.component_id = c.id LEFT JOIN system.t_component cc ON c.parent_id = cc.id LEFT JOIN system.t_bridge_info b ON b.id=bc.bridge_info_id LEFT JOIN system.t_dictionary d ON t.task_status = d.`code` LEFT JOIN system.t_dictionary dd ON t.task_type =dd.`code` WHERE 1 = 1
AND t.task_status = #{taskQueryVO.taskStatus}
AND t.task_type= #{taskQueryVO.taskType}
group by t.id

mapper.update:
update system.t_task t
set t.inspector_id =#{userId},t.task_status ='1002000001'
where t.id=#{taskId}
